import React, {Fragment, useState, useEffect} from 'react';
import Swal from 'sweetalert2';
import { withRouter } from 'react-router-dom'; 
import clienteAxios from '../../config/axios';



function NuevoTicket({history}){

    useEffect( () => {

      
            // Query a la API
            const consultarAPI = async () => {
                try {
                    const clientesConsulta = await clienteAxios.get('/clientes', {
                        headers: {
                            Authorization : `Bearer ${auth.token}`
                        }
                    });
    
                    // colocar el resultado en el state
                    guardarClientes(clientesConsulta.data);

                } catch (error) {
                    // Error con authorizacion
                    if(error.response.status = 500) {
                        props.history.push('/iniciar-sesion');
                    }
                }
            }
            consultarAPI();
        
    }, [] );


   

    // cliente = state, guardarcliente = funcion para guardar el state
    const[cliente, guardarCliente] = useState({
        Subject: '',
        Description: '',
        start : '',
        dateend: ''
    });

    // leer los datos del formulario
    const actualizarState = e => {
        // Almacenar lo que el usuario escribe en el state
        guardarCliente({
            // obtener una copia del state actual
            ...cliente, 
            [e.target.name] : e.target.value
        })

    }

    // Añade en la REST API un cliente nuevo
    const agregarCliente = e => {
        e.preventDefault();

        // enviar petición
        clienteAxios.post('/clientes', cliente)
            .then(res => {
                // validar si hay errores de mongo 
                if(res.data.code === 11000) {
                    Swal.fire({
                        type: 'error',
                        title: 'Hubo un error',
                        text: 'Ese cliente ya esta registrado'
                    })
                } else {
                    Swal.fire(
                        'Se agregó el Cliente',
                        res.data.mensaje,
                        'success'
                    )
                }
                // Redireccionar
                history.push('/');
            });
    }

    // Validar el formulario
    const validarCliente = () => {
        // Destructuring
        const {Subject, Description, start, dateend, user} = cliente;

        // revisar que las propiedades del state tengan contenido
        let valido = !Subject.length || !Description.length || !start.length || !dateend.length;

        // return true o false
        return valido;
    }

    //asignar usuario
    const asignar = ()=>{

        console.log("llego")
    }

    // verificar si el usuario esta autenticado o no
    if((localStorage.getItem('token') ==='' ) ) {
        history.push('/iniciar-sesion');
    }

    const [foodState, setFoodState] = useState("");

    return (


        <Fragment>
            <h2>Nuevo Ticket</h2>
            
            <form
                onSubmit={agregarCliente}
            >
                <legend>Llena todos los campos</legend>
                <div className="campo">
                    <label>Subject:</label>
                    <input  type="text" 
                            placeholder="Subject" 
                            name="Subject"
                            onChange={actualizarState}
                    />
                </div>

                <div className="campo">
                    <label>Description:</label>
                    <input type="text" 
                          placeholder="Description" 
                          name="Description" 
                          onChange={actualizarState}
                    />
                </div>
            
                <div className="campo">
                    <label>date start:</label>
                    <input type="date" 
                          placeholder="Empresa Cliente" 
                          name="start" 
                          onChange={actualizarState}
                    />
                </div>

                <div className="campo">
                    <label>date end:</label>
                    <input type="date" 
                            placeholder="Email Cliente" 
                            name="dateend" 
                            onChange={actualizarState}
                    />
                </div>

           

                <div className="campo left">
                    
                  <label>Usuario:</label>
                    <select
                        className="campo"
                        name="user"
                        value=""
                        onChange={(e) => {
                        const selectedFood = e.target.value;
                        setFoodState(selectedFood);
                        }}
                    >
                        
                        <option value="1">Steak</option>
                        <option value="2">Sandwich</option>
                        <option value="3">Dumpling</option>
                    </select>

                    <input className="right "
                        type="submit"
                        value="+" 
                        disabled={ asignar() }
                    />
                  
                </div>
               
               


                <div className="enviar">
                    <input 
                        type="submit" 
                        className="btn btn-azul" 
                        value="Crear Ticket" 
                        disabled={ validarCliente() }
                    />
                </div>
            </form>
        </Fragment>
    )
}

// HOC, es una función que toma un componente y retorna un nuevo componente
export default  withRouter(NuevoTicket);
