                    history.push('/');
                });*/
        }
    


    return (
        <Fragment>

            <h2>Report</h2>

            <form
                onSubmit={agregarCliente}
            >
            <div className="campo">
                    <label>date start:</label>
                    <input type="date" 
                          placeholder="Empresa Cliente" 
                          name="start" 
                          onChange={actualizarState}
                    />
                </div>

                <div className="campo">
                    <label>date end:</label>
                    <input type="date" 
                            placeholder="Email Cliente" 
                            name="dateend" 
                            onChange={actualizarState}
                    />
                </div>

            </form>

        </Fragment>
       
     )

}

export default withRouter(Report);
